<?php
namespace GuzzleHttp\Event; use GuzzleHttp\Transaction; use GuzzleHttp\ClientInterface; use GuzzleHttp\Message\RequestInterface; abstract class AbstractRequestEvent extends AbstractEvent { protected $transaction; public function __construct(Transaction $transaction) { $this->transaction = $transaction; } public function getClient() { return $this->transaction->client; } public function getRequest() { return $this->transaction->request; } public function getRetryCount() { return $this->transaction->retries; } public function getTransaction() { return $this->transaction; } } 